import React, { Component } from 'react';
import socketIOClient from "socket.io-client";
import './App.css';

const span = React.createElement('span', {}, "zdre")

class App extends Component {
  constructor(){
    super()
    this.state = {
      response: false,
      endpoint: "http://localhost:3001",
      itemArray:[1,2,3]
    }
  }
  componentDidMount(){
    const {endpoint} = this.state;
    const socket = socketIOClient(endpoint);
    socket.on("data", data => {
    this.setState({
       response: data 
      })
    }
    );
  }
  createElement = (event) => {
    console.log("X ===", event.clientX)
    console.log("Y ===", event.clientY)
    const items = this.state.itemArray
    const positionX = event.clientX
    const positionY = event.clientY
    let posObj = {
      x:positionX,
      y:positionY
    }
    items.push(posObj)
    this.setState({
      itemArray: items
    })
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  componentWillMount(){
    window.addEventListener('click', this.createElement)
  }
  render() {
    const { response } = this.state;
    return (
      <div className="App">
        <h1>{response ? "CONNECTED" : "NOT CONNECTED" }</h1>
        {this.state.itemArray.map((res, index) => {
          if(res.x !== undefined){
            return <input className="pos" onChange={this.state.handleChange} key={index} value={res.x} />
          }
        })}
      </div>
    );
  }
}

export default App;
